if (!DATAVIZ) var DATAVIZ = {};

DATAVIZ.Main = function() {
    var that = this;
    this.angularApp = angular.module("dataviz", []), $(document).ready(function() {
        that.setListeners();
    });
}, DATAVIZ.Main.prototype.setListeners = function() {
    $(".gridster ul").gridster({
        widget_margins: [ 10, 10 ],
        widget_base_dimensions: [ 140, 140 ]
    });
}, window.DATAVIZ = new DATAVIZ.Main(), angular.module("dataviz").controller("textWidget", function($scope) {
    $scope.template = {
        name: "template1.html",
        url: "templates/text.html"
    };
});